#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Syntax
#--------------------------------------------------------------------------------------------------

if [ $# != 1 ] || [ $1 != "win32" ]; then

    echo "Usage: generate <win32>"

    exit 1
fi

#--------------------------------------------------------------------------------------------------
# Archive
#--------------------------------------------------------------------------------------------------

echo "COMPRESSING ZIP"

archive="3rdparty-$1.zip"

rm -f $archive

path=3rdparty/$1

rm $path/.gitignore

7z a $archive "3rdparty/$1"

touch $path/.gitignore
